package pattern5;

public class pattern5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=4,a=1;
        for (int i=1;i<=n;i++) 
        {
            for (int j=1;j<=i;j++) 
            {
                System.out.print(" "+a);
                a++; 
            }
            System.out.println();
        }
    }    
}



