package pattern8;

public class pattern8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 5;
		int j;
		for(int i=1;i<=n;i++)
		{
			for(j=1;j<i;j++)
			{
				System.out.print(" "); 
			}
			for(j=1;j<=n;j++)
			{
				System.out.print("*"); 
			}
			System.out.println();
		}
	}
}






	


