package week2;

public class occurence {

		public static void main(String[] args) {
			// TODO Auto-generated method stub
			        int a[]={1,1,2,2,2,2,3};
			        int x=2;
			        int c=0;
			        for(int i=0;i<a.length;i++)
			        {
			            if(a[i]==x)
			            c=c+1;
			        }
			        System.out.println(c);

		}
}
